window.addEventListener("DOMContentLoaded",()=>{let n=document.createElement("div");n.innerHTML='\n    <style>\n      #jb-container {\n        display: flex;\n        justify-content: space-between;\n        align-items: center;\n        padding: 20px;\n        font-family: sans-serif;\n        background-color: #071315;\n        color: #b6bdc8;\n        width: 100%;\n        max-width: 100vw;\n        box-sizing: border-box;\n        position: relative;\n        bottom: 0;\n        margin: 0;\n        border: 0;\n      }\n      #jb-container * {\n        margin: 0;\n        padding: 0;\n        border: 0;\n        font-size: 100%;\n        font: inherit;\n        vertical-align: baseline;\n      }\n      #jb-container a {\n        text-decoration: none;\n        color: #b6bdc8;\n        display: flex;\n        align-items: center;\n        font-size: 14px;\n        width: 100%; \n        justify-content: space-between\n      }\n      #jb-container a:hover {\n        text-decoration: underline;\n      }\n      #jb-container img {\n        width: 125px; \n        max-width: 100%;\n      }\n      @media (max-width: 600px) {\n        #jb-container a {\n          width: 100%;\n          flex-direction: column;\n          text-align: center;\n        }\n      }\n    </style>\n    <div id="jb-container" role="contentinfo">\n      <a href="https://jbtoner.com" target="_blank" aria-label="Hosted by jbtoner.com">\n        <p>Hosted by jbtoner.com</p>\n        <img src="jbtonercom-footerlogo.svg" alt="JB Toner Logo">\n      </a>\n    </div>',document.body.appendChild(n)});